import React, { Component } from 'react';
import { Link } from 'react-router-dom';

export default class Login extends Component {

	constructor(props) {
        super(props);
        this.state = {
			email: "",
            password: ""
        };
    }

	handleLoginIn = () => {

		const formData = new FormData();
		formData.append('employee_id', 7);//todo get logged in user
		formData.append('punch_status', 'active');
		formData.append('punch_out_report', null);

		// API call to punch-in
		// API call to add break
		fetch(`${process.env.REACT_APP_API_URL}/reports.php?action=add`, {
			method: "POST",
			body: formData,
		})
			.then((response) => response.json())
			.then((data) => {
				if (data.status === "success") {
					this.setState({ isPunchedIn: true });
				} else {
					alert(data.message);
					console.log("Failed to add punch-in data");
				}
			})
			.catch((error) => console.error("Error:", error));
	};

	// Handle input change
	handlePasswordChange = (event) => {
		this.setState({ password: event.target.value });
	};

	// Handle input change
	handlePasswordChange = (event) => {
		this.setState({ password: event.target.value });
	};

	render() {
		return (
			<div className="auth">
				<div className="auth_left">
					<div className="card">
						<div className="text-center mb-2">
							<Link className="header-brand" to="/">
								<i className="fe fe-command brand-logo" />
							</Link>
						</div>
						<div className="card-body">
							<div className="card-title">Login to your account</div>
							{/* <div className="form-group">
								<select className="custom-select">
									<option>HR Dashboard</option>
									<option>Project Dashboard</option>
									<option>Job Portal</option>
								</select>
							</div> */}
							<div className="form-group">
								<input
									type="email"
									className="form-control"
									id="exampleInputEmail1"
									aria-describedby="emailHelp"
									placeholder="Enter email"
								/>
							</div>
							<div className="form-group">
								<label className="form-label">
									{/* Password */}
									{/* <Link className="float-right small" to="/forgotpassword">
										I forgot password
									</Link> */}
								</label>
								<input
									type="password"
									className="form-control"
									id="exampleInputPassword1"
									placeholder="Password"
								/>
							</div>
							{/* <div className="form-group">
								<label className="custom-control custom-checkbox">
									<input type="checkbox" className="custom-control-input" />
									<span className="custom-control-label">Remember me</span>
								</label>
							</div> */}
							<div className="form-footer">
								<a className="btn btn-primary btn-block" href="#" onClick={this.handleLoginIn}>
									Click to login
								</a>
							</div>
						</div>
					</div>
				</div>
				<div className="auth_right">
					<div className="carousel slide" data-ride="carousel" data-interval={3000}>
						<div className="carousel-inner">
							<div className="carousel-item active">
								<img src="assets/images/slider1.svg" className="img-fluid" alt="login page" />
								<div className="px-4 mt-4">
									<h4>Fully Responsive</h4>
									<p>Lorem ipsum dolor sit amet, consectetur adipisicing elit.</p>
								</div>
							</div>
							<div className="carousel-item">
								<img src="assets/images/slider2.svg" className="img-fluid" alt="login page" />
								<div className="px-4 mt-4">
									<h4>Quality Code and Easy Customizability</h4>
									<p>There are many variations of passages of Lorem Ipsum available.</p>
								</div>
							</div>
							<div className="carousel-item">
								<img src="assets/images/slider3.svg" className="img-fluid" alt="login page" />
								<div className="px-4 mt-4">
									<h4>Cross Browser Compatibility</h4>
									<p>Overview We're a group of women who want to learn JavaScript.</p>
								</div>
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	}
}
