import React, { Component } from 'react'
import { connect } from 'react-redux';
import CountUp from 'react-countup';
import Modal from 'react-modal';

class Report extends Component {

    constructor(props) {
        super(props);
        this.state = {
            reports: [],
            error: null,
            selectedReport: null,
            isModalOpen: false
        };
    }

    componentDidMount() {
        // Make the GET API call when the component is mounted
        fetch(`${process.env.REACT_APP_API_URL}/reports.php`)
            .then(response => response.json())
            .then(data => {
                if (data.status === 'success') {
                    this.setState({ reports: data.data });
                } else {
                    this.setState({ error: data.message });
                }
            })
            .catch(err => {
                this.setState({ error: 'Failed to fetch data' });
                console.error(err);
            });
    }

    openModal = (report) => {
        this.setState({ selectedReport: report, isModalOpen: true });
    };

    closeModal = () => {
        this.setState({ selectedReport: null, isModalOpen: false });
    };

    render() {
        const { fixNavbar } = this.props;
        const { reports, selectedReport, isModalOpen, error } = this.state;
        // Define custom styles for the modal
        const customStyles = {
            overlay: {
                position: 'fixed',
                top: 0,
                left: 0,
                right: 0,
                bottom: 0,
                backgroundColor: 'rgba(0, 0, 0, 0.5)', // Semi-transparent black background
            },
            content: {
                position: 'absolute',
                top: '20%',
                left: '50%',
                transform: 'translateX(-50%)',
                width: '80%', // Adjust width as needed
                maxWidth: '600px', // Maximum width
                height: 'auto',
                maxHeight: '80%', // Maximum height
                overflow: 'auto',
                border: '1px solid #ccc',
                background: '#fff',
                borderRadius: '4px',
                outline: 'none',
                padding: '20px',
            },
        };
        return (
            <>
                <div>
                    <div className={`section-body ${fixNavbar ? "marginTop" : ""}`}>
                        <div className="container-fluid">
                            <div className="d-flex justify-content-between align-items-center">
                                <ul className="nav nav-tabs page-header-tab">
                                </ul>
                                <div className="header-action d-md-flex">
                                    <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#addReportModal"><i className="fe fe-plus mr-2" />Add</button>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="section-body mt-3">
                        <div className="container-fluid">
                            <div className="tab-content mt-3">
                                <div className="tab-pane fade show active" id="Report-Invoices" role="tabpanel">
                                    <div className="card">
                                        <div className="card-body">
                                            <div className="table-responsive">
                                                <table className="table table-hover table-striped table-vcenter mb-0">
                                                    <thead>
                                                        <tr>
                                                            <th>Employee Name</th>
                                                            <th>Employee ID</th>
                                                            <th>Attendance Date</th>
                                                            <th>Punch In</th>
                                                            <th>Punch Out</th>
                                                            <th>Total Hours Worked</th>
                                                            <th>Attendance Status</th>
                                                            <th>Action</th>
                                                        </tr>
                                                    </thead>
                                                    <tbody>
                                                        {reports.length > 0 ? (
                                                            reports.map((report, index) => (
                                                                <tr>
                                                                    <td>{report.employee_name}</td>
                                                                    <td>{report.employee_id}</td>
                                                                    <td>{report.attendance_date}</td>
                                                                    <td>{report.punch_in_time}</td>
                                                                    <td>{report.punch_out_time}</td>
                                                                    <td>{report.total_hours_worked}</td>
                                                                    <td>{report.attendance_status}</td>
                                                                    <td>
                                                                        <button
                                                                            className="btn btn-info"
                                                                            onClick={() => this.openModal(report)}
                                                                        >
                                                                            View Report
                                                                        </button>
                                                                    </td>
                                                                </tr>
                                                            ))
                                                        ) : (
                                                            <tr>
                                                                <td colSpan="8" style={{ textAlign: 'center' }}>
                                                                    {error ? error : 'No reports available.'}
                                                                </td>
                                                            </tr>
                                                        )}
                                                    </tbody>
                                                </table>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    {/* Modal for viewing report details */}
                    {selectedReport && (
                        <Modal
                            isOpen={isModalOpen}
                            onRequestClose={this.closeModal}
                            contentLabel="Report Details"
                            ariaHideApp={false}
                            style={customStyles}
                        >
                            <h2>Report Details</h2>
                            {this.state.selectedReport && (
                                <div className="multiline-text">
                                    <p>{this.state.selectedReport.report}</p>
                                    {/* Display other details as needed */}
                                </div>
                            )}
                            <button className="btn btn-secondary" onClick={this.closeModal}>
                                Close
                            </button>
                        </Modal>
                    )}
                </div>
            </>
        )
    }
}
const mapStateToProps = state => ({
    fixNavbar: state.settings.isFixNavbar
})

const mapDispatchToProps = dispatch => ({})
export default connect(mapStateToProps, mapDispatchToProps)(Report);
