import React, { Component } from 'react';
import { connect } from 'react-redux';
import EditEmployee from './EditEmployee';

class AddEmployee extends Component {
    constructor(props) {
        super(props);
        this.state = {
            firstName: "",
            lastName: "",
            email: "",
            gender: "",
            photo: null,
            dob: "",
            joiningDate: "",
            mobile1: "",
            mobile2: "",
            address1: "",
            address2: "",
            emergencyContact1: "",
            emergencyContact2: "",
            emergencyContact3: "",
            skillsFrontend: [],
            skillsBackend: [],
            bankAccountName: "",
            bankAccountNo: "",
            bankName: "",
            ifscCode: "",
            bankAddress: "",
            salaryDetails: [
                { salarySource: "", salaryAmount: "", fromDate: "", toDate: "" },
            ],
            aadharCardNumber: "",
            aadharCardFile: "",
            drivingLicenseNumber: "",
            drivingLicenseFile: "",
            panCardNumber: "",
            panCardFile: "",
            facebook: "",
            twitter: "",
            linkedin: "",
            instagram: "",
            upworkProfile: "",
            resume: null,
        };
    }
    handleChange = (e) => {
        const { name, value } = e.target;
        this.setState({
          [name]: value,
        });
    };

    handleSalaryDetails = (index, field, value) => {
        this.setState((prevState) => {
            const updatedEntries = [...prevState.salaryDetails];
            updatedEntries[index][field] = value;
            return { salaryDetails: updatedEntries };
        });
    };

    // Add more salary details
    addMore = () => {
        this.setState((prevState) => ({
            salaryDetails: [...prevState.salaryDetails, { salarySource: "", salaryAmount: "", fromDate: "", toDate: "" }],
        }));
    };

    // Remove an entry
    removeEntry = (index) => {
        const salaryDetails = this.state.salaryDetails.filter((_, i) => i !== index);
        this.setState({ salaryDetails });
    };

    handleFileChange = (e) => {
        const { name, files } = e.target;
        this.setState({
          [name]: files[0],
        });
    };

    handleSkillChange = (e, category) => {
        const { value, checked } = e.target;
        this.setState((prevState) => {
          const updatedSkills = checked
            ? [...prevState[category], value]
            : prevState[category].filter((skill) => skill !== value);

          return { [category]: updatedSkills };
        });
    };

    handleSubmit = (e) => {
        e.preventDefault();
        console.log("User Data:", this.state);
        // Add your form submission logic here
    };

    handleBack = () => {
        this.props.history.goBack(); // Navigate to the previous page
    };

    demoEntries = [
        {
            salarySource: "HDFC Bank",
            salaryAmount: 50000,
            fromDate: "2023-01-01",
            toDate: "2023-12-31",
        },
        {
            salarySource: "Cash Payout",
            salaryAmount: 30000,
            fromDate: "2023-06-01",
            toDate: "2023-12-31",
        },
    ];

    render() {
		const { fixNavbar } = this.props;
        const { firstName, lastName, email, gender, dob, joiningDate, mobile1, mobile2, address1, address2, emergencyContact1, emergencyContact2, emergencyContact3, skillsFrontend, skillsBackend,  bankAccountName, bankAccountNo, bankName, ifscCode, bankAddress, salaryDetails, aadharCardNumber, drivingLicenseNumber, panCardNumber, facebook, twitter, linkedin, instagram, upworkProfile} = this.state;

        // const { skillsFrontend, skillsBackend } = this.state;
        // Frontend and Backend Skill Options
        const frontendSkills = ["HTML", "CSS", "JavaScript", "React", "Angular", "Vue"];
        const backendSkills = ["PHP", "Laravel", "Python", "Node.js", "Symfony", "Django", "Ruby on Rails"];

		return (
            <>
                <EditEmployee salaryDetails={demoEntries} />
                <div>
					<div className={`section-body ${fixNavbar ? "marginTop" : ""}`}>
						<div className="container-fluid">
							<div className="row">
								<div className="col-12 col-lg-12">
                                    <form className="card" onSubmit={this.handleSubmit}>
                                        <div className="card-body">
                                            <h3 className="card-title">Add Employee</h3>
                                            <div className="row">
                                                {/* <div className="col-md-4">
                                                    <div className="form-group">
                                                        <label className="form-label">Employee ID</label>
                                                        <input type="text" className="form-control" placeholder="Employee ID" />
                                                    </div>
                                                </div> */}
                                                <div className="col-sm-6 col-md-6">
                                                    <div className="form-group">
                                                        <label className="form-label" htmlFor="firstName">First Name</label>
                                                        <input
                                                            type="text"
                                                            name="firstName"
                                                            id="firstName"
                                                            className="form-control"
                                                            placeholder="Enter First Name"
                                                            value={firstName}
                                                            onChange={this.handleChange}
                                                            required
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-6">
                                                    <div className="form-group">
                                                        <label className="form-label">Last Name</label>
                                                        <input
                                                            type="text"
                                                            name="lastName"
                                                            id='lastName'
                                                            className="form-control"
                                                            placeholder="Enter Last Name"
                                                            value={lastName}
                                                            onChange={this.handleChange}
                                                            required
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-4">
                                                    <div className="form-group">
                                                        <label className="form-label">Email address</label>
                                                        <input
                                                            type="email"
                                                            name="email"
                                                            id='email'
                                                            className="form-control"
                                                            placeholder="Enter Email Address"
                                                            value={email}
                                                            onChange={this.handleChange}
                                                            required
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-4">
                                                    <div className="form-group">
                                                        <label className="form-label">Gender</label>
                                                        <select 
                                                            name="gender"
                                                            className="form-control"
                                                            id='gender'
                                                            value={gender}
                                                            onChange={this.handleChange}
                                                            required
                                                        >
                                                            <option value="">Select Gender</option>
                                                            <option value="male" >Male</option>
                                                            <option value="female" >Female</option>
                                                        </select>
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-4">
                                                    <div className="form-group">
                                                        <label className="form-label">Photo</label>
                                                        <input
                                                            type="file"
                                                            name="photo"
                                                            className="form-control"
                                                            onChange={this.handleFileChange}
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-4">
                                                    <div className="form-group">
                                                        <label className="form-label">DOB</label>
                                                        <input
                                                            type="date"
                                                            id="dob"
                                                            name="dob"
                                                            className="form-control"
                                                            value={dob}
                                                            onChange={this.handleChange}
                                                            required
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-4">
                                                    <div className="form-group">
                                                        <label className="form-label">Joining Date</label>
                                                        <input
                                                            type="date"
                                                            id="joiningDate"
                                                            name="joiningDate"
                                                            className="form-control"
                                                            value={joiningDate}
                                                            onChange={this.handleChange}
                                                            required
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-6">
                                                    <div className="form-group">
                                                        <label className="form-label">Mobile No (1)</label>
                                                        <input
                                                            type="tel"
                                                            name="mobile1"
                                                            id="mobile1"
                                                            className="form-control"
                                                            placeholder="Enter Mobile No"
                                                            value={mobile1}
                                                            onChange={this.handleChange}
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-6">
                                                    <div className="form-group">
                                                        <label className="form-label">Mobile No (2)</label>
                                                        <input
                                                            type="tel"
                                                            name="mobile2"
                                                            id="mobile2"
                                                            className="form-control"
                                                            placeholder="Enter Mobile No"
                                                            value={mobile2}
                                                            onChange={this.handleChange}
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-md-12">
                                                    <div className="form-group">
                                                        <label className="form-label">Address Line 1</label>
                                                        <input
                                                            type="text"
                                                            name="address1"
                                                            id="address1"
                                                            className="form-control"
                                                            placeholder="Enter Address Line 1"
                                                            value={address1}
                                                            onChange={this.handleChange}
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-md-12">
                                                    <div className="form-group">
                                                        <label className="form-label">Address Line 2</label>
                                                        <input
                                                            type="text"
                                                            name="address2"
                                                            id="address2"
                                                            className="form-control"
                                                            placeholder="Enter Address Line 2"
                                                            value={address2}
                                                            onChange={this.handleChange}
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-4 mb-4">
                                                    <label className="form-label">Emergency Contact 1</label>
                                                    <input
                                                        type="tel"
                                                        name="emergencyContact1"
                                                        id="emergencyContact1"
                                                        className="form-control"
                                                        placeholder="Enter Emergency Contact"
                                                        value={emergencyContact1}
                                                        onChange={this.handleChange}
                                                    />
                                                </div>
                                                <div className="col-sm-6 col-md-4 mb-4">
                                                    <label className="form-label">Emergency Contact 2</label>
                                                    <input
                                                        type="tel"
                                                        name="emergencyContact2"
                                                        id="emergencyContact2"
                                                        className="form-control"
                                                        placeholder="Enter Emergency Contact"
                                                        value={emergencyContact2}
                                                        onChange={this.handleChange}
                                                    />
                                                </div>
                                                <div className="col-sm-6 col-md-4 mb-4">
                                                    <label className="form-label">Emergency Contact 3</label>
                                                    <input
                                                        type="tel"
                                                        name="emergencyContact3"
                                                        id="emergencyContact3"
                                                        className="form-control"
                                                        placeholder="Enter Emergency Contact"
                                                        value={emergencyContact3}
                                                        onChange={this.handleChange}
                                                    />
                                                </div>

                                                {/* Frontend Skills */}
                                                <div className="row mb-4">
                                                    <h5 className="w-100">Skills</h5>
                                                    <div className="col-sm-6 col-md-12">
                                                        <label className="form-label">Frontend</label>
                                                        <div className="d-flex flex-wrap">
                                                            {frontendSkills.map((skill) => (
                                                                <label key={skill} className="mr-3 mb-2">
                                                                <input
                                                                    type="checkbox"
                                                                    // defaultValue={`${this.getColor(skill)}`}
                                                                    value={skill}
                                                                    checked={skillsFrontend.includes(skill)}
                                                                    onChange={(e) => this.handleSkillChange(e, "skillsFrontend")}
                                                                />{" "}
                                                                <span className={`tag tag-${this.getColor(skill)} py-1 px-2`}>{skill}</span>
                                                                </label>
                                                            ))}
                                                        </div>
                                                    </div>

                                                    {/* Backend Skills */}
                                                    <div className="col-sm-6 col-md-12">
                                                        <label className="form-label">Backend</label>
                                                        <div className="d-flex flex-wrap">
                                                            {backendSkills.map((skill) => (
                                                                <label key={skill} className="mr-3 mb-2">
                                                                <input
                                                                    type="checkbox"
                                                                    value={skill}
                                                                    checked={skillsBackend.includes(skill)}
                                                                    onChange={(e) => this.handleSkillChange(e, "skillsBackend")}
                                                                />{" "}
                                                                <span className={`tag tag-${this.getColor(skill)} py-1 px-2`}>{skill}</span>
                                                                </label>
                                                            ))}
                                                        </div>
                                                    </div>
                                                </div>

                                                {/* Bank Account Detail */}
                                                <div className="row mb-4">
                                                    <h5 className="w-100">Bank Account Details</h5>
                                                    <div className="col-sm-6 col-md-6">
                                                        <div className="form-group">
                                                            <label className="form-label">Account Holder Name</label>
                                                            <input
                                                                type="text"
                                                                name="bankAccountName"
                                                                id="bankAccountName"
                                                                className="form-control"
                                                                placeholder="Account Holder Name"
                                                                value={bankAccountName}
                                                                onChange={this.handleChange}
                                                            />
                                                        </div>
                                                    </div>
                                                    <div className="col-sm-6 col-md-6">
                                                        <div className="form-group">
                                                            <label className="form-label">Account Number</label>
                                                            <input
                                                                type="number"
                                                                name="bankAccountNo"
                                                                id="bankAccountNo"
                                                                className="form-control"
                                                                placeholder="Account Number"
                                                                value={bankAccountNo}
                                                                onChange={this.handleChange}
                                                            />
                                                        </div>
                                                    </div>
                                                    <div className="col-sm-6 col-md-4">
                                                        <div className="form-group">
                                                            <label className="form-label">Bank Name</label>
                                                            <input
                                                                type="text"
                                                                name="bankName"
                                                                id="bankName"
                                                                className="form-control"
                                                                placeholder="Bank Name"
                                                                value={bankName}
                                                                onChange={this.handleChange}
                                                            />
                                                        </div>
                                                    </div>
                                                    <div className="col-sm-6 col-md-4">
                                                        <div className="form-group">
                                                            <label className="form-label">IFSC Code</label>
                                                            <input
                                                                type="text"
                                                                name="ifscCode"
                                                                id="ifscCode"
                                                                className="form-control"
                                                                placeholder="IFSC Code"
                                                                value={ifscCode}
                                                                onChange={this.handleChange}
                                                            />
                                                        </div>    
                                                    </div>
                                                    <div className="col-sm-6 col-md-4">
                                                        <div className="form-group">
                                                            <label className="form-label">Bank Address</label>
                                                            <input
                                                                type="text"
                                                                name="bankAddress"
                                                                id="bankAddress"
                                                                className="form-control"
                                                                placeholder="Bank Address"
                                                                value={bankAddress}
                                                                onChange={this.handleChange}
                                                            />
                                                        </div>
                                                    </div>
                                                </div>

                                                {/* Salary Details */}
                                                <div className="row mb-4">
                                                    <h5 className="w-100">Salary Details</h5>
                                                    {salaryDetails.map((entry, index) => (
                                                        <div key={index} className='w-100'>
                                                            <div className="row">
                                                                <div className="col-sm-6 col-md-3">
                                                                    <div className="form-group">
                                                                        <label className="form-label">Salary Source</label>
                                                                        <select 
                                                                            name="salarySource"
                                                                            className="form-control"
                                                                            id='salarySource'
                                                                            value={entry.salarySource}
                                                                            onChange={(e) => this.handleSalaryDetails(index, "salarySource", e.target.value)}
                                                                        >
                                                                            <option value="">Select</option>
                                                                            <option value="IDBI Bank" >IDBI Bank</option>
                                                                            <option value="HDFC Bank" >HDFC Bank</option>
                                                                            <option value="Cash Payout" >Cash Payout</option>
                                                                            <option value="NEFT" >NEFT</option>
                                                                        </select>
                                                                    </div>
                                                                </div>
                                                                <div className="col-sm-6 col-md-3">
                                                                    <div className="form-group">
                                                                        <label className="form-label">Salary Amount</label>
                                                                        <input
                                                                            type="number"
                                                                            name="salaryAmount"
                                                                            id="salaryAmount"
                                                                            className="form-control"
                                                                            placeholder="Enter salary amount"
                                                                            value={entry.salaryAmount}
                                                                            onChange={(e) => this.handleSalaryDetails(index, "salaryAmount", e.target.value)}
                                                                        />
                                                                    </div>
                                                                </div>
                                                                <div className="col-sm-6 col-md-2">
                                                                    <div className="form-group">
                                                                        <label className="form-label">From</label>
                                                                        <input
                                                                            type="date"
                                                                            name="fromDate"
                                                                            id="fromDate"
                                                                            className="form-control"
                                                                            value={entry.fromDate}
                                                                            onChange={(e) => this.handleSalaryDetails(index, "fromDate", e.target.value)}
                                                                        />
                                                                    </div>
                                                                </div>
                                                                <div className="col-sm-6 col-md-2">
                                                                    <div className="form-group">
                                                                        <label className="form-label">To</label>
                                                                        <input
                                                                            type="date"
                                                                            name="toDate"
                                                                            id="toDate"
                                                                            className="form-control"
                                                                            value={entry.toDate}
                                                                            onChange={(e) => this.handleSalaryDetails(index, "toDate", e.target.value)}
                                                                        />
                                                                    </div>    
                                                                </div>
                                                                {salaryDetails.length > 1 && (
                                                                    <div className="col-sm-6 col-md-2">
                                                                        <div className="form-group">
                                                                            <button
                                                                                type="button"
                                                                                title="Delete"
                                                                                className="btn btn-icon"
                                                                                style={{
                                                                                    marginTop: "1.2em",
                                                                                    fontSize: "1.2em",
                                                                                }}
                                                                                onClick={() => this.removeEntry(index)}
                                                                            >
                                                                                <i className="fa fa-trash-o text-danger" />
                                                                            </button>
                                                                        </div>
                                                                    </div>
                                                                )}
                                                            </div>
                                                        </div>
                                                    ))}
                                                    <div className="col-sm-6 col-md-4">
                                                        <div className="form-group">
                                                            <button type="button" className="btn btn-primary" onClick={this.addMore} >
                                                                Add More
                                                            </button>
                                                        </div>
                                                    </div>
                                                </div>

                                                {/* Govt Issue ID */}
                                                <div className="row mb-4">
                                                    <h5 className="w-100">Govt Issue ID</h5>
                                                    <div className="col-sm-6 col-md-4">
                                                        <div className="form-group">
                                                            <label className="form-label">Aadhar Card</label>
                                                            <input
                                                                type="text"
                                                                name="aadharCardNumber"
                                                                id="aadharCardNumber"
                                                                className="form-group form-control"
                                                                placeholder="Aadhar Card Number"
                                                                value={aadharCardNumber}
                                                                onChange={this.handleChange}
                                                            />
                                                            <input
                                                                type="file"
                                                                name="aadharCard"
                                                                className="form-control"
                                                                placeholder="Aadhar Card"
                                                                onChange={this.handleFileChange}
                                                            />
                                                        </div>
                                                    </div>
                                                    <div className="col-sm-6 col-md-4">
                                                        <div className="form-group">
                                                            <label className="form-label">Driving License</label>
                                                            <input
                                                                type="text"
                                                                name="drivingLicenseNumber"
                                                                id="drivingLicenseNumber"
                                                                className="form-group form-control"
                                                                placeholder="Driving License Number"
                                                                value={drivingLicenseNumber}
                                                                onChange={this.handleChange}
                                                            />
                                                            <input
                                                                type="file"
                                                                name="drivingLicense"
                                                                className="form-control"
                                                                placeholder="Driving License"
                                                                onChange={this.handleFileChange}
                                                            />
                                                        </div>
                                                    </div>
                                                    <div className="col-sm-6 col-md-4">
