import React, { Component } from 'react';
import { connect } from 'react-redux';

class EditEmployee extends Component {
    constructor(props) {
        super(props);
        // Assuming the employee data is passed as props or initialized in state
        this.state = {
            firstName: this.props.employee.firstName || '',
            lastName: this.props.employee.lastName || '',
            email: this.props.employee.email || '',
            gender: this.props.employee.gender || '',
            photo: this.props.employee.photo || '',
            dob: this.props.employee.dob || '',
            joiningDate: this.props.employee.joiningDate || '',
            mobile1: this.props.employee.mobile1 || '',
            mobile2: this.props.employee.mobile2 || '',
            address1: this.props.employee.address1 || '',
            address2: this.props.employee.address2 || '',
            emergencyContact1: this.props.employee.emergencyContact1 || '',
            emergencyContact2: this.props.employee.emergencyContact2 || '',
            emergencyContact3: this.props.employee.emergencyContact3 || '',
            skillsFrontend: this.props.employee.skillsFrontend || [],
            skillsBackend: this.props.employee.skillsBackend || [],
            bankAccountName: this.props.employee.bankAccountName || '',
            bankAccountNo: this.props.employee.bankAccountNo || '',
            bankName: this.props.employee.bankName || '',
            ifscCode: this.props.employee.ifscCode || '',
            bankAddress: this.props.employee.bankAddress || '',
            aadharCardNumber: this.props.employee.aadharCardNumber || '',
            aadharCardFile: this.props.employee.aadharCardFile || '',
            drivingLicenseNumber: this.props.employee.drivingLicenseNumber || '',
            drivingLicenseFile: this.props.employee.drivingLicenseFile || '',
            panCardNumber: this.props.employee.panCardNumber || '',
            panCardFile: this.props.employee.panCardFile || '',
            facebook: this.props.employee.facebook || '',
            twitter: this.props.employee.twitter || '',
            linkedin: this.props.employee.linkedin || '',
            instagram: this.props.employee.instagram || '',
            upworkProfile: this.props.employee.upworkProfile || '',
            resume: this.props.employee.resume || '',
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleFileChange = this.handleFileChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleSkillChange = this.handleSkillChange.bind(this);
        this.handleBack = this.handleBack.bind(this);
    }
    handleChange = (e) => {
        const { name, value } = e.target;
        this.setState({
          [name]: value,
        });
    };

    handleFileChange = (e) => {
        const { name, files } = e.target;
        this.setState({
          [name]: files[0],
        });
    };

    handleSkillChange = (e, skillType) => {
        const { value, checked } = e.target;
        this.setState((prevState) => {
            const skills = prevState[skillType];
            if (checked) {
                return { [skillType]: [...skills, value] };
            } else {
                return { [skillType]: skills.filter(skill => skill !== value) };
            }
        });
    };

    handleSubmit = (e) => {
        e.preventDefault();
        console.log("Form Submitted:", this.state);
        // Add your form submission logic here
    };

    handleBack = () => {
        this.props.history.goBack(); // Navigate to the previous page
    };

    render() {
        const { fixNavbar } = this.props;
        const { firstName, lastName, email, gender, dob, joiningDate, mobile1, mobile2, address1, address2, emergencyContact1, emergencyContact2, emergencyContact3, skillsFrontend, skillsBackend,  bankAccountName, bankAccountNo, bankName, ifscCode, bankAddress, aadharCardNumber,
        drivingLicenseNumber, panCardNumber, facebook, twitter, linkedin, instagram, upworkProfile} = this.state;

        // const { skillsFrontend, skillsBackend } = this.state;
        // Frontend and Backend Skill Options
        const frontendSkills = ["HTML", "CSS", "JavaScript", "React", "Angular", "Vue"];
        const backendSkills = ["PHP", "Laravel", "Python", "Node.js", "Symfony", "Django", "Ruby on Rails"];

        return (
            <>
                <div>
                    <div className={`section-body ${fixNavbar ? "marginTop" : ""}`}>
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-12 col-lg-12">
                                    <form className="card" onSubmit={this.handleSubmit}>
                                        <div className="card-body">
                                            <h3 className="card-title">Edit Employee</h3>
                                            <div className="row">
