import React, { Component } from 'react';
import { connect } from 'react-redux';
import Ckeditor from '../../common/ckeditor';
import { useState } from "react";
class Activities extends Component {
	constructor(props) {
		super(props);
		this.state = {
			activities: [],
			error: null,
			employeeData: [],
			selectedStatus: "" // State for dropdown selection
		};
	}

	componentDidMount() {
		// Make the GET API call when the component is mounted
		// fetch('http://localhost/react/epicpro-backend/users.php')
		fetch(`${process.env.REACT_APP_API_URL}/activities.php`)
			.then(response => response.json())
			.then(data => {
				if (data.status === 'success') {
					this.setState({ activities: data.data });
				} else {
					this.setState({ error: data.message });
				}
			})
			.catch(err => {
				this.setState({ error: 'Failed to fetch data' });
				console.error(err);
			});

		/** Get employees list */
		fetch(`${process.env.REACT_APP_API_URL}/get_employees.php`)
			.then(response => response.json())
			.then(data => {
				if (data.status === 'success') {
					this.setState({ employeeData: data.data });
				} else {
					this.setState({ error: data.message });
				}
			})
			.catch(err => {
				this.setState({ error: 'Failed to fetch data' });
				console.error(err);
			});
	}

	// Handle dropdown change
	handleStatusChange = (event) => {
		this.setState({ selectedStatus: event.target.value });
	};

	addBreak = () => {

        // Validate form inputs
        if (!employeeId || !firstName || !email || !username) {
            alert("Please fill in all fields");
            return;
        }

        const addUserData = new FormData();
        addUserData.append('employee_id', employeeId);
        addUserData.append('first_name', firstName);
        addUserData.append('last_name', lastName);
        addUserData.append('email', email);
        addUserData.append('mobile_no', mobileNo);
        addUserData.append('selected_role', selectedRole);
        addUserData.append('username', username);
        addUserData.append('password', confirmPassword);

        // API call to add user
        fetch(`${process.env.REACT_APP_API_URL}/users.php?action=add`, {
            method: "POST",
            body: addUserData,
        })
        .then((response) => response.json())
        .then((data) => {
            if (data.status === "success") {
                // Update the department list
                this.setState((prevState) => ({
                    users: [...(prevState.users || []), data.data], // Assuming the backend returns the new department
                    employeeId: "",
                    firstName: "",
                    lastName: "",
					email: "",
					mobileNo: "",
					selectedRole:"",
					username: "",
					password: "",
					confirmPassword: ""
                }));
            } else {
                console.log("Failed to add user");
            }
        })
        .catch((error) => console.error("Error:", error));
    };

	render() {
		const { fixNavbar } = this.props;
		const { activities, error, employeeData, selectedStatus } = this.state;
		return (
			<>
				{/* <link rel="stylesheet" href="../assets/plugins/summernote/dist/summernote.css" /> */}
				<div>
					<div className={`section-body ${fixNavbar ? "marginTop" : ""} mt-3`}>
						<div className="container-fluid">
							<div className="row clearfix">
								<div className="col-md-12">
									<div className="card">
										<div className="card-header">
											<h3 className="card-title">Timeline Activity</h3>
											<button type="button" className="btn btn-primary" data-toggle="modal" data-target="#addBreakModal"><i className="fe fe-plus mr-2" />Add Break</button>
										</div>
										<div className="card-body">
											<div className="summernote">
											</div>
											{activities.length > 0 ? (
												activities.map((activity, index) => (
													<div className="timeline_item ">
														<img
															className="tl_avatar"
															src="../assets/images/xs/avatar1.jpg"
															alt="fake_url"
														/>
														<span>
															<a href="#">{activity.first_name} {activity.last_name}</a> {activity.location}
															<small className="float-right text-right">
																{activity.formatted_date}
															</small>
														</span>
														<h6 className="font600">
															({activity.break_status}) {activity.break_reason}
														</h6>
													</div>
												))
											) : (
												error && <p>{error}</p>
											)}
										</div>
									</div>
								</div>
							</div>
						</div>
					</div>

					{/* Add Break Modal */}
					<div className="modal fade" id="addBreakModal" tabIndex={-1} role="dialog" aria-labelledby="addBreakModalLabel" /* aria-hidden="true" */>
						<div className="modal-dialog" role="break">
							<div className="modal-content">
								<div className="modal-header">
									<h5 className="modal-title" id="addBreakModalLabel">Add Break</h5>
									<button type="button" className="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">×</span></button>
								</div>
								<div className="modal-body">
									<div className="row clearfix">
										<div className="col-md-12">
											<div className="form-group">
												<select className="form-control show-tick" name="employee" id="emp_id">
												<option value="">Select Employee</option>
													{employeeData.length > 0 ? (
													employeeData.map((employee, index) => (
												      <option value={employee.id}>{`${employee.first_name} ${employee.last_name}`}</option>
																))
												) : (
													!error && <option value="">Select Employee</option>
												)}
												</select>
											</div>
										</div>
										<div className="col-md-12">
											<div className="form-group">
												<select className="form-control show-tick" name="status" id="break_status" value={selectedStatus} onChange={this.handleStatusChange}>
													<option value="">Select Status</option>
													<option value="active">Break In</option>
													<option value="completed">Break Out</option>
												</select>
											</div>
										</div>
										{selectedStatus === "active" && (
										<div className="col-md-12">
											<div className="form-group">
											<textarea className="form-control" name="reason" placeholder="Reason" aria-label="With textarea" id="break_reason" />
											</div>
										</div>
										)}
									</div>
								</div>
								<div className="modal-footer">
									<button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
									<button type="button" className="btn btn-primary" onClick={this.addBreak}>Save changes</button>
								</div>
							</div>
						</div>
					</div>

				</div>
			</>
		);
	}
}
const mapStateToProps = state => ({
	fixNavbar: state.settings.isFixNavbar
})

const mapDispatchToProps = dispatch => ({})
export default connect(mapStateToProps, mapDispatchToProps)(Activities);
