import React, { Component } from 'react';
import { connect } from 'react-redux';

class EditEmployee extends Component {
    constructor(props) {
        super(props);
        const { selectedEmployee = {} } = this.props;
        // Assuming the employee data is passed as props or initialized in state
        this.state = {
            firstName: '',
            lastName: '',
            email: '',
            gender: '',
            photo: null,
            dob: '',
            joiningDate: '',
            mobile1: '',
            mobile2: '',
            address1: '',
            address2: '',
            emergencyContact1: '',
            emergencyContact2: '',
            emergencyContact3: '',
            skillsFrontend: [],
            skillsBackend: [],
            bankAccountName: '',
            bankAccountNo: '',
            bankName: '',
            ifscCode: '',
            bankAddress: '',
            salaryDetails: [],
            aadharCardNumber: '',
            aadharCardFile: null,
            drivingLicenseNumber: '',
            drivingLicenseFile: null,
            panCardNumber: '',
            panCardFile: null,
            facebook: '',
            twitter: '',
            linkedin: '',
            instagram: '',
            upworkProfile: '',
            resume: null,
            selectedEmployee
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleFileChange = this.handleFileChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleSkillChange = this.handleSkillChange.bind(this);
        this.handleBack = this.handleBack.bind(this);
    }

    handleChange = (event) => {
		const { name, value } = event.target;

		// Update state for the selected user
        this.setState((prevState) => ({
            selectedEmployee: {
                ...prevState.selectedEmployee,
                [name]: value,
            },
        }));
    };

    handleSalaryDetails = (index, field, value) => {
        this.setState((prevState) => {
            const updatedEntries = [...this.state.salaryDetails];
            updatedEntries[index][field] = value;
            return {
                salaryDetails: updatedEntries,
                selectedEmployee: {
                    ...prevState.selectedEmployee,
                    salaryDetails: updatedEntries, // Add updated salaryDetails to selectedEmployee
                },
            };
        });
    };

    handleFileChange = (e) => {
        const { name, files } = e.target;
        this.setState((prevState) => ({
            selectedEmployee: {
                ...prevState.selectedEmployee,
                [name]: files[0],
            },
        }));
    };

    handleSkillChange = (e, skillType) => {
        const { value, checked } = e.target;
        console.log(frontendSkills);
        this.setState((prevState) => {
            // Get the current list of skills from selectedEmployee or default to an empty array
            const currentSkills = prevState.selectedEmployee[skillType] || [];

            let updatedSkills;
            if (checked) {
                // Add the skill to the array if not already present
                updatedSkills = [...currentSkills, value];
            } else {
                // Remove the skill from the array if unchecked
                updatedSkills = currentSkills.filter(skill => skill !== value);
            }

            // Return the updated state with the updated skills list
            return {
                selectedEmployee: {
                    ...prevState.selectedEmployee,
                    [skillType]: updatedSkills,
                },
            };
        });
    };        

    /* handleSkillChange = (e, skillType) => {
        const { value, checked } = e.target;
        this.setState((prevState) => {
            const skills = prevState[skillType] || []; // Default to an empty array if it's undefined
            if (checked) {
                return { [skillType]: [...skills, value] };
            } else {
                return { [skillType]: skills.filter(skill => skill !== value) };
            }
        });
    }; */

    componentDidMount() {
        const { employee, selectedSalaryDetails = [] } = this.props.location.state || {}; // Retrieve employee data

        if (selectedSalaryDetails) {
            // Transform salaryDetails if necessary
            const formattedSalaryDetails = selectedSalaryDetails.map((detail) => ({
                salarySource: detail.source || "",
                salaryAmount: detail.amount || 0,
                fromDate: detail.from_date || "",
                toDate: detail.to_date || "",
            }));

            // Store formatted data in state
            this.setState({ salaryDetails: formattedSalaryDetails });
        } else {
            console.error("No salary details found in location state.");
        }

        this.setState((prevState) => ({
            firstName: employee.first_name,
            lastName: employee.last_name,
            email: employee.email,
            gender: employee.gender,
            photo: employee.profile,
            dob: employee.dob,
            joiningDate: employee.joining_date,
            mobile1: employee.mobile_no1,
            mobile2: employee.mobile_no2,
            address1: employee.address_line1,
            address2: employee.address_line2,
            emergencyContact1: employee.emergency_contact1,
            emergencyContact2: employee.emergency_contact2,
            emergencyContact3: employee.emergency_contact3,
            skillsFrontend: employee.frontend_skills,
            skillsBackend: employee.backend_skills,
            bankAccountName: employee.account_holder_name,
            bankAccountNo: employee.account_number,
            bankName: employee.bank_name,
            ifscCode: employee.ifsc_code,
            bankAddress: employee.bank_address,
            aadharCardNumber: employee.aadhar_card_number,
            aadharCardFile: employee.aadhar_card_file,
            drivingLicenseNumber: employee.driving_license_number,
            drivingLicenseFile: employee.driving_license_file,
            panCardNumber: employee.pan_card_number,
            panCardFile: employee.pan_card_file,
            facebook: employee.facebook_url,
            twitter: employee.twitter_url,
            linkedin: employee.linkedin_url,
            instagram: employee.instagram_url,
            upworkProfile: employee.upwork_profile_url,
            resume: employee.resume,
        }));
    }

    handleSubmit = (e) => {
        e.preventDefault();
        console.log("Form Submitted:", this.state);
        // Add your form submission logic here
    };

    handleBack = () => {
        this.props.history.goBack(); // Navigate to the previous page
    };

    updateEmployee  = () => {
        const { selectedEmployee, salaryDetails } = this.state;
        if (!selectedEmployee) return;

		const updateEmployeeData = new FormData();
        updateEmployeeData.append('first_name', selectedEmployee.firstName);
        updateEmployeeData.append('last_name', selectedEmployee.lastName);
        updateEmployeeData.append('email', selectedEmployee.email);
        updateEmployeeData.append('gender', selectedEmployee.gender);
        updateEmployeeData.append('photo', selectedEmployee.photo);
        updateEmployeeData.append('dob',selectedEmployee.dob);
        updateEmployeeData.append('joining_date',selectedEmployee.joiningDate);
        updateEmployeeData.append('mobile_no1',selectedEmployee.mobile1);
        updateEmployeeData.append('mobile_no2',selectedEmployee.mobile2);
        updateEmployeeData.append('address_line1', selectedEmployee.address1);
        updateEmployeeData.append('address_line2', selectedEmployee.address2);
        updateEmployeeData.append('emergency_contact1', selectedEmployee.emergencyContact1);
        updateEmployeeData.append('emergency_contact2', selectedEmployee.emergencyContact2);
        updateEmployeeData.append('emergency_contact3', selectedEmployee.emergencyContact3);
        updateEmployeeData.append('frontend_skills', selectedEmployee.skillsFrontend);
        updateEmployeeData.append('backend_skills', selectedEmployee.skillsBackend);
        updateEmployeeData.append('account_holder_name', selectedEmployee.bankAccountName);
        updateEmployeeData.append('account_number', selectedEmployee.bankAccountNo);
        updateEmployeeData.append('bank_name', selectedEmployee.bankName);
        updateEmployeeData.append('ifsc_code', selectedEmployee.ifscCode);
        updateEmployeeData.append('bank_address', selectedEmployee.bankAddress);
        salaryDetails.forEach((detail, index) => {
            updateEmployeeData.append(`salaryDetails[${index}][source]`, detail.salarySource);
            updateEmployeeData.append(`salaryDetails[${index}][amount]`, detail.salaryAmount);
            updateEmployeeData.append(`salaryDetails[${index}][from_date]`, detail.fromDate);
            updateEmployeeData.append(`salaryDetails[${index}][to_date]`, detail.toDate);
        });        
        updateEmployeeData.append('aadhar_card_number', selectedEmployee.aadharCardNumber);
        updateEmployeeData.append('aadhar_card_file', selectedEmployee.aadharCardFile);
        updateEmployeeData.append('driving_license_number', selectedEmployee.drivingLicenseNumber);
        updateEmployeeData.append('driving_license_file', selectedEmployee.drivingLicenseFile);
        updateEmployeeData.append('pan_card_number', selectedEmployee.panCardNumber);
        updateEmployeeData.append('pan_card_file', selectedEmployee.panCardFile);
        updateEmployeeData.append('facebook_url', selectedEmployee.facebook);
        updateEmployeeData.append('twitter_url', selectedEmployee.twitter);
        updateEmployeeData.append('linkedin_url', selectedEmployee.linkedin);
        updateEmployeeData.append('instagram_url', selectedEmployee.instagram);
        updateEmployeeData.append('upwork_profile_url', selectedEmployee.upworkProfile);
        updateEmployeeData.append('resume', selectedEmployee.resume);

        fetch(`http://localhost/react/epicpro-backend/get_employees.php?action=edit&user_id=${selectedEmployee.id}`, {
            method: "POST",
            body: updateEmployeeData,
        })
        .then((response) => response.json())
        .then((data) => {
            console.log('data = ', data);
            if (data.status === "success") {
                // Update the department list
                this.setState((prevState) => ({
                    //users: [...(prevState.users || []), data.data], // Assuming the backend returns the new department
                    firstName: "",
                    lastName: "",
                    email: "",
                    gender: "",
                    photo: null,
                    dob: "",
                    joiningDate: "",
                    mobile1: "",
                    mobile2: "",
                    address1: "",
                    address2: "",
                    emergencyContact1: "",
                    emergencyContact2: "",
                    emergencyContact3: "",
                    skillsFrontend: [],
                    skillsBackend: [],
                    bankAccountName: "",
                    bankAccountNo: "",
                    bankName: "",
                    ifscCode: "",
                    bankAddress: "",
                    salaryDetails: [
                        { salarySource: "", salaryAmount: "", fromDate: "", toDate: "" },
                    ],
                    aadharCardNumber: "",
                    aadharCardFile: null,
                    drivingLicenseNumber: "",
                    drivingLicenseFile: null,
                    panCardNumber: "",
                    panCardFile: null,
                    facebook: "",
                    twitter: "",
                    linkedin: "",
                    instagram: "",
                    upworkProfile: "",
                    resume: null,
                }));

                // Clear all file inputs
                Object.values(this.fileInputRefs).forEach((ref) => {
                    if (ref.current) ref.current.value = "";
                });
            } else {
                console.log("Failed to add department");
            }
        })
        .catch((error) => console.error("Error:", error));
    }

    render() {
        const { fixNavbar } = this.props;
        const { firstName, lastName, email, gender, dob, joiningDate, mobile1, mobile2, address1, address2, emergencyContact1, emergencyContact2, emergencyContact3, skillsFrontend, skillsBackend,  bankAccountName, bankAccountNo, bankName, ifscCode, bankAddress, salaryDetails, aadharCardNumber,
        drivingLicenseNumber, panCardNumber, facebook, twitter, linkedin, instagram, upworkProfile} = this.state;
        // Frontend and Backend Skill Options
        const frontendSkills = ["HTML", "CSS", "JavaScript", "React", "Angular", "Vue"];
        const backendSkills = ["PHP", "Laravel", "Python", "Node.js", "Symfony", "Django", "Ruby on Rails"];

        return (
            <>
                <div>
                    <div className={`section-body ${fixNavbar ? "marginTop" : ""}`}>
                        <div className="container-fluid">
                            <div className="row">
                                <div className="col-12 col-lg-12">
                                    <form className="card" onSubmit={this.handleSubmit}>
                                        <div className="card-body">
                                            <h3 className="card-title">Edit Employee</h3>
                                            <div className="row">
                                                <div className="col-sm-6 col-md-6">
                                                    <div className="form-group">
                                                        <label className="form-label" htmlFor="firstName">First Name</label>
                                                        <input
                                                            type="text"
                                                            name="firstName"
                                                            id="firstName"
                                                            className="form-control"
                                                            placeholder="Enter First Name"
                                                            value={firstName}
                                                            onChange={this.handleChange}
                                                            required
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-6">
                                                    <div className="form-group">
                                                        <label className="form-label">Last Name</label>
                                                        <input
                                                            type="text"
                                                            name="lastName"
                                                            id='lastName'
                                                            className="form-control"
                                                            placeholder="Enter Last Name"
                                                            value={lastName}
                                                            onChange={this.handleChange}
                                                            required
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-4">
                                                    <div className="form-group">
                                                        <label className="form-label">Email address</label>
                                                        <input
                                                            type="email"
                                                            name="email"
                                                            id='email'
                                                            className="form-control"
                                                            placeholder="Enter Email Address"
                                                            value={email}
                                                            onChange={this.handleChange}
                                                            required
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-4">
                                                    <div className="form-group">
                                                        <label className="form-label">Gender</label>
                                                        <select 
                                                            name="gender"
                                                            className="form-control"
                                                            id='gender'
                                                            value={gender}
                                                            onChange={this.handleChange}
                                                            required
                                                        >
                                                            <option value="">Select Gender</option>
                                                            <option value="male" >Male</option>
                                                            <option value="female" >Female</option>
                                                        </select>
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-4">
                                                    <div className="form-group">
                                                        <label className="form-label">Photo</label>
                                                        <input
                                                            type="file"
                                                            name="photo"
                                                            className="form-control"
                                                            onChange={this.handleFileChange}
                                                            accept="image/*"
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-4">
                                                    <div className="form-group">
                                                        <label className="form-label">DOB</label>
                                                        <input
                                                            type="date"
                                                            id="dob"
                                                            name="dob"
                                                            className="form-control"
                                                            value={dob}
                                                            onChange={this.handleChange}
                                                            required
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-4">
                                                    <div className="form-group">
                                                        <label className="form-label">Joining Date</label>
                                                        <input
                                                            type="date"
                                                            id="joiningDate"
                                                            name="joiningDate"
                                                            className="form-control"
                                                            value={joiningDate}
                                                            onChange={this.handleChange}
                                                            required
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-6">
                                                    <div className="form-group">
                                                        <label className="form-label">Mobile No (1)</label>
                                                        <input
                                                            type="tel"
                                                            name="mobile1"
                                                            id="mobile1"
                                                            className="form-control"
                                                            placeholder="Enter Mobile No"
                                                            value={mobile1}
                                                            onChange={this.handleChange}
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-sm-6 col-md-6">
                                                    <div className="form-group">
                                                        <label className="form-label">Mobile No (2)</label>
                                                        <input
                                                            type="tel"
                                                            name="mobile2"
                                                            id="mobile2"
                                                            className="form-control"
                                                            placeholder="Enter Mobile No"
                                                            value={mobile2}
                                                            onChange={this.handleChange}
                                                        />
                                                    </div>
                                                </div>
                                                <div className="col-md-12">
                                                    <div className="form-group">
                                                        <label className="form-label">Address Line 1</label>
                                                        <input
                                                            type="text"
                                                            name="address1"
                                                            id="address1"
