import React, { Component } from 'react';
import { connect } from 'react-redux';
import Ckeditor from '../../common/ckeditor';
class Activities extends Component {
	constructor(props) {
		super(props);
		this.state = {
			activities: [],
			error: null
		};
	}

	componentDidMount() {
		// Make the GET API call when the component is mounted
		// fetch('http://localhost/react/epicpro-backend/users.php')
		fetch(`${process.env.REACT_APP_API_URL}/activities.php`)
		.then(response => response.json())
		.then(data => {
			if (data.status === 'success') {
			  	this.setState({ activities: data.data });
			} else {
			  	this.setState({ error: data.message });
			}
		})
		.catch(err => {
			this.setState({ error: 'Failed to fetch data' });
			console.error(err);
		});
	}

	render() {
		const { fixNavbar } = this.props;
		const { activities, error } = this.state;
		return (
			<>
				{/* <link rel="stylesheet" href="../assets/plugins/summernote/dist/summernote.css" /> */}
				<div>
					<div className={`section-body ${fixNavbar ? "marginTop" : ""} mt-3`}>
						<div className="container-fluid">
							<div className="row clearfix">
								<div className="col-md-12">
									<div className="card">
										<div className="card-header">
											<h3 className="card-title">Timeline Activity</h3>
												<button type="button" className="btn btn-primary" data-toggle="modal" data-target="#addBreakModal"><i className="fe fe-plus mr-2" />Add Break</button>
										</div>
										<div className="card-body">
											<div className="summernote">
											</div>
											{activities.length > 0 ? (
												activities.map((activity, index) => (
											<div className="timeline_item ">
												<img
													className="tl_avatar"
													src="../assets/images/xs/avatar1.jpg"
													alt="fake_url"
												/>
												<span>
													<a href="#">{activity.first_name} {activity.last_name}</a> {activity.location}
													<small className="float-right text-right">
														{activity.formatted_date}
													</small>
												</span>
												<h6 className="font600">
												({activity.break_status}) {activity.break_reason}
												</h6>
											</div>
                                             ))
											): (
												error && <p>{error}</p>
											)}
										</div>
									</div>
								</div>
							</div>
						</div>
					</div>

					{/* Add Break Modal */}
                    <div className="modal fade" id="addBreakModal" tabIndex={-1} role="dialog" aria-labelledby="addBreakModalLabel" /* aria-hidden="true" */>
                        <div className="modal-dialog" role="break">
                            <div className="modal-content">
                                <div className="modal-header">
                                    <h5 className="modal-title" id="addBreakModalLabel">Add Break</h5>
                                    <button type="button" className="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">Ã—</span></button>
                                </div>
                                <div className="modal-body">
                                    <div className="row clearfix">
                                        <div className="col-md-12">
                                            <div className="form-group">
											<select className="form-control show-tick">
												<option>Select Employee</option>
												<option value="super_admin">Super Admin</option>
											</select>
                                            </div>
                                        </div>
                                        <div className="col-md-12">
                                            <div className="form-group">
											<select className="form-control show-tick">
												<option>Select Break Status</option>
												<option value="active">Break In</option>
												<option value="completed">Break Out</option>
											</select>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="modal-footer">
                                    <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                                    <button type="button" onClick={this.addDepartmentData} className="btn btn-primary">Save changes</button>
                                </div>
                            </div>
                        </div>
                    </div>

				</div>
			</>
		);
	}
}
const mapStateToProps = state => ({
	fixNavbar: state.settings.isFixNavbar
})

const mapDispatchToProps = dispatch => ({})
export default connect(mapStateToProps, mapDispatchToProps)(Activities);
